class Address {
  String? receiverName;
  String? houseFlatNumber;
  String? flatName;
  String? nearOrOpposite;
  String? areaName;
  String? city;
  String? postalCode;
  String? country;

  Address({
    required this.receiverName,
    required this.houseFlatNumber,
    required this.flatName,
    required this.nearOrOpposite,
    required this.areaName,
    required this.city,
    required this.postalCode,
    required this.country,
  });
}
